mapper.update:
update employee_work_attendance_t


        start_time = #{startTime},


        end_time = #{endTime},


        start_ip = #{startIp},


        end_ip = #{endIp},




        and emp_id = #{empId}


        and emp_name = #{empName}


and attendance_time = #{attendanceTime}

mapper.select:
select att. from employee_work_attendance_t att, ( select * from employee_staff_user_t
user_id = #{userId}
) user where delete_flag = 0 and user.emp_id = att.emp_id and attendance_time = DATE_FORMAT(now(),'%Y-%m-%d') order by attendance_time desc

select att. from employee_work_attendance_t att
att.emp_id in (
#{item.empId}
)
and delete_flag = 0 order by attendance_time desc,id asc
